import httplib2
c = httplib2.HTTPSConnectionWithTimeout("docs.python.org",443) #443 is HTTPS default port...optional to be specified
#c = httplib2.HTTPConnectionWithTimeout("www.gnu.org")
#https://docs.python.org/3/tutorial/index.html (Redirecting to this url)
c.putrequest("GET", "/3/tutorial/index.html")
c.putheader("someheader", "somevalue")
c.endheaders()

r = c.getresponse()
data = r.read()
print(data)

c.close()


